        // SemiCircle Progress Component
        const SemiCircleProgress = ({ percentage, label, width = 150, height = 80 }) => {
            const canvasRef = useRef(null);
        
            useEffect(() => {
                const canvas = canvasRef.current;
                const ctx = canvas.getContext("2d");
                const centerX = width / 2;
                const centerY = height;
                const radius = Math.min(width, height) * 0.45;
                const startAngle = Math.PI;     // слева (180°)
                const endAngle = 2 * Math.PI;   // справа (360°)
        
                ctx.clearRect(0, 0, width, height);
        
                // фон (серая дуга)
                ctx.beginPath();
                ctx.arc(centerX, centerY, radius, startAngle, endAngle);
                ctx.strokeStyle = "#e5e7eb";
                ctx.lineWidth = 8;
                ctx.stroke();
        
                if (percentage <= 100) {
                    // обычный прогресс
                    const progressAngle = startAngle + (percentage / 100) * Math.PI;
                    ctx.beginPath();
                    ctx.arc(centerX, centerY, radius, startAngle, progressAngle);
                    ctx.strokeStyle =
                        percentage >= 90 ? "#16a34a" : percentage >= 50 ? "#eab308" : "#dc2626";
                    ctx.lineWidth = 8;
                    ctx.stroke();
                } else {
                    // зелёная дуга до 100%
                    ctx.beginPath();
                    ctx.arc(centerX, centerY, radius, startAngle, endAngle);
                    ctx.strokeStyle = "#16a34a";
                    ctx.lineWidth = 8;
                    ctx.stroke();
        
                    // синяя дуга сверху — начинается СЛЕВА и идёт вправо
                    const extraPercent = Math.min(percentage - 100, 50); // максимум 50%
                    if (extraPercent > 0) {
                        const extraAngle = startAngle + (extraPercent / 100) * Math.PI;
                        ctx.beginPath();
                        ctx.arc(centerX, centerY, radius, startAngle, extraAngle);
                        ctx.strokeStyle = "#2563eb";
                        ctx.lineWidth = 8;
                        ctx.stroke();
                    }
                }
        
                // текст
                ctx.font = `${Math.floor(width / 10)}px Arial`;
                ctx.fillStyle = "#1f2937";
                ctx.textAlign = "center";
                ctx.textBaseline = "middle";
                ctx.fillText(`${percentage.toFixed(1)}%`, centerX, centerY - radius - 15);
            }, [percentage, width, height]);
        
            return (
                <div className="flex flex-col items-center transition-transform duration-300 hover:scale-105">
                    <canvas ref={canvasRef} width={width} height={height} />
                    <p className="text-sm font-medium text-gray-600 mt-2">{label}</p>
                </div>
            );
        };


        
            


