        // ---- fetch daily hours + trainings ----
        async function fetchDailyHoursAndTrainings() {
            if (!user || !selectedSvId) return;
            setIsLoading(true);
            try {
            const hoursParams = new URLSearchParams();
            hoursParams.append('month', month);
            // If a supervisor is selected, request data for that supervisor by id
            // (allow 'sv' users to request other supervisors the same as admin)
            if (selectedSvId) {
                hoursParams.append('id', selectedSvId);
            } else if (user.role === 'sv') {
                hoursParams.append('id', String(user.id));
            }

            const trainingsParams = new URLSearchParams();
            trainingsParams.append('month', month);
            if (selectedSvId) {
                trainingsParams.append('id', selectedSvId);
            } else if (user.role === 'sv') {
                trainingsParams.append('id', String(user.id));
            }

            const url = `${API_BASE_URL}/api/sv/daily_hours?${hoursParams.toString()}`;
            const trainingsUrl = `${API_BASE_URL}/api/trainings?${trainingsParams.toString()}`;

            const [hoursResp, trainingsResp] = await Promise.all([
                axios.get(url, { headers: { 'X-API-Key': user.apiKey, 'X-User-Id': user.id } }),
                axios.get(trainingsUrl, { headers: { 'X-API-Key': user.apiKey, 'X-User-Id': user.id } })
            ]);

            if (hoursResp.data && hoursResp.data.status === 'success' && Array.isArray(hoursResp.data.operators)) {
                setOperators(hoursResp.data.operators);
            } else {
                setOperators([]);
                fallbackToast('Не удалось загрузить daily hours', 'error');
            }

            const trainingsList = Array.isArray(trainingsResp.data) ? trainingsResp.data : (trainingsResp.data?.trainings || []);
            const tmap = {};
            for (const t of trainingsList) {
                const op = t.operator_id;
                let dayNum;
                try {
                dayNum = new Date(t.date + 'T00:00:00').getDate();
                } catch (e) {
                dayNum = parseInt(t.date.slice(-2), 10);
                }
                if (!tmap[op]) tmap[op] = {};
                if (!tmap[op][dayNum]) tmap[op][dayNum] = [];
                tmap[op][dayNum].push(t);
            }
            setTrainingsMap(tmap);

            } catch (err) {
            console.error('fetchDailyHoursAndTrainings error', err);
            fallbackToast('Ошибка загрузки данных', 'error');
            setOperators([]);
            setTrainingsMap({});
            } finally {
            setIsLoading(false);
            }
        }

