            const getBaigaUserStats = () => {
                if (!currentUser?.id && !currentUser?.name) return null;
                
                const allEntries = [...contestData.baiga.groups['0.5'], ...contestData.baiga.groups['0.75'], ...contestData.baiga.groups['1']];
                
                let userEntry = null;
                let overallPosition = -1;
                let groupPosition = -1;
                
                // Поиск по ID
                if (currentUser.id) {
                    userEntry = allEntries.find(p => p.id === currentUser.id);
                }
                
                // Если не нашли по ID, ищем по имени
                if (!userEntry && currentUser.name) {
                    const normalizedUserName = (currentUser.name || '').toLowerCase().trim();
                    userEntry = allEntries.find(p => (p.name || '').toLowerCase().trim() === normalizedUserName);
                }
                
                if (!userEntry) return null;
                
                // Находим позицию в общем рейтинге
                const sortedAll = [...allEntries].sort((a, b) => (b.scores || 0) - (a.scores || 0));
                overallPosition = sortedAll.findIndex(p => p.id === userEntry.id || (p.name || '').toLowerCase() === (userEntry.name || '').toLowerCase()) + 1;
                
                // Находим позицию в группе
                const groupEntries = contestData.baiga.groups[userEntry.group] || [];
                groupPosition = groupEntries.findIndex(p => p.id === userEntry.id || (p.name || '').toLowerCase() === (userEntry.name || '').toLowerCase()) + 1;
                
                return {
                    entry: userEntry,
                    overallPosition,
                    groupPosition,
                    totalInGroup: groupEntries.length,
                    totalOverall: allEntries.length
                };
            };

