            const audioUrlCache = {};
            const getAudioUrlWithAuth = async (evaluationId, apiKey, userId) => {
                if (audioUrlCache[evaluationId]) {
                    return audioUrlCache[evaluationId];
                }
            
                try {
                    const response = await fetch(`${API_BASE_URL}/api/audio/${evaluationId}`, {
                        method: 'GET',
                        credentials: 'include',
                        headers: withAccessTokenHeader({
                            'X-API-Key': apiKey,
                            'X-User-Id': userId
                        })
                    });
            
                    if (!response.ok) throw new Error(`Failed to fetch audio: ${response.status}`);
            
                    const data = await response.json();
                    if (data && data.url) {
                        audioUrlCache[evaluationId] = data.url;
                        return data.url;
                    } else {
                        console.error("No signed URL returned in response:", data);
                        return null;
                    }
                } catch (error) {
                    console.error('Audio fetch error:', error);
                    return null;
                }
            };

            const handleEvaluationClick = async (evaluation) => {
                if (expandedEvaluation?.id === evaluation.id) {
                    setExpandedEvaluation(null);
                    setAudioUrl(null);
                    return;
                }

                setExpandedEvaluation(evaluation);
                const canLoadAudio = !!operatorData?.sensitive_access?.granted;
                if (!canLoadAudio) {
                    setAudioUrl(null);
                    setLoadingAudioId(null);
                    return;
                }
                setLoadingAudioId(evaluation.id);
                const url = await getAudioUrlWithAuth(evaluation.id, user.apiKey, user.id);
                setAudioUrl(url);
                setLoadingAudioId(null);
            };


            const getScoreColor = (score) => {
                if (!score) return 'text-gray-500';
                if (score >= 90) return 'text-green-600';
                if (score >= 60) return 'text-yellow-600';
                return 'text-red-600';
            };

            const getCallCountColor = (count, expected) => {
                if (count >= expected) return 'text-green-600';
                if (count >= expected * 0.5) return 'text-yellow-500';
                return 'text-red-600';
            };

            const getHoursPerformanceColor = (hours, norm) => {
                if (hours >= norm * 0.9) return 'text-green-600';
                if (hours >= norm * 0.5) return 'text-yellow-600';
                return 'text-red-600';
            };

            const getCallsPerHourColor = (CPH) => {
                if (CPH >= 20) return 'text-green-600';
                if (CPH >= 10) return 'text-yellow-600';
                return 'text-red-600';
            };

            const getCurrentWeekOfMonth = () => {
                const today = new Date();
                const firstDay = new Date(today.getFullYear(), today.getMonth(), 1);
                const diff = today.getDate() + firstDay.getDay() - 1;
                return Math.ceil(diff / 7);
            };

